  .diff-table > tbody > tr[data-hunk]:hover > td,
  .highlight > tbody > tr:hover > td {
    position: relative;
  }
  .diff-table > tbody > tr[data-hunk]:hover > td::after,
  .highlight > tbody > tr:hover > td::after {
    content: "";
    position: absolute;
    left: 0;
    right: 0;
    top: 0;
    bottom: 0;
    background: rgba(255, 255, 255, .075);
    pointer-events: none;
  }
